            <input
            className={classes.userInfo} placeholder='Username'
            ></input>
          </div>
          <div
            style={{
              margin: 15,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              backgroundColor: '#DFD6CF',
              width: '30%',
              height: '40px',
              borderRadius: 90,
              fontSize: '30px',
              fontWeight: 'bold',
              cursor: 'pointer',
            }}
          >
            <input  className={classes.userInfo} placeholder='Password'></input> 
         </div>
        </div>
      </div>
    </div>
  );
}
