import React from 'react'
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import ButtonBase from '@material-ui/core/ButtonBase';
import '../Style.css'
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
    },
    resultButton:{
        backgroundColor: '#2A363B',
        color: '#FECEAB',
        fontSize: '20px'
    },
    resultImageWrapper: {
        width: '125px',
        height: '125px',
        // backgroundColor: 'black',
        //padding: '10px'
    },
    searchResultPic: {
        width:'200px',
        height:'200px',
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
        objectFit: 'cover'
    }
  }));



export default function SearchResults(props){
    const classes = useStyles();
    const {results} = props
    return(
        <>
        {results && results.map((result)=>(
            <div key ={result.id}>
                <Paper spacing = {0} style={{backgroundColor: '#FECEAB', marginTop: '15px', height: '200px', padding: '0px'}} >
                    <Grid item sm container spacing ={0} style={{height: '100%'}}>
                            <Grid item xs={1}  container direction='column' justify='flex-start' alignItems="flex-start">
                                <Grid item xs>
                                    <ButtonBase className={classes.resultImageWrapper}>
                                        <img alt="MovieImage" className = {classes.searchResultPic} src = {`https://image.tmdb.org/t/p/w500${result.poster_path}`}/>
                                    </ButtonBase>     
                                </Grid>                      
                            </Grid>
                        <Grid item sm container style={{height: '100%'}}>
                            <Grid item xs={8} container direction="column" > 
                                <Grid item xs>
                                    <h1 style={{marginTop:0, marginBottom:10}}>{result.title}</h1>
                                </Grid>
                                    <Grid item xs container drection="row">
                                        <Grid item xs>
                                            Average Rating: 
                                        </Grid>
                                        <Grid item xs>
                                            Friend Rating: 
                                        </Grid>
                                    </Grid>
                                <Grid item xs>
                                    {result.overview}
                                </Grid>
                                <Grid style={{fontWeight: 'bold', marginTop:10}}>
                                    Watched by: BradyMan18, MayaD, and 12 other friends
                                </Grid>
                            </Grid>
                            <Grid style={{padding: '20px', backgroundColor: '#E84A5F'}} item xs={4} container direction="column" alignItems="stretch" justify="space-between" > 
                                <Button className={classes.resultButton}>Add to List</Button>
                                <Button className={classes.resultButton}>Write Review</Button>
                                <Button className={classes.resultButton}>Full Description</Button>
                            </Grid>
                        </Grid>
                    </Grid>
                </Paper>
                {/* <h1 >{result.title}</h1>
                <h2>Average Rating: {result.vote_average}</h2>
                <p>{result.overview}</p> */}
            </div>
        ))}
        </>
    )
}
