import {React, useEffect, useState} from 'react'
import ReviewList from '../Components/ReviewList'
import Paper from '@material-ui/core/Paper';
import { firestore } from '../firebase/firebase.util';
import { useLocation} from 'react-router-dom'
import '../Style.css';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  searchResultPic: {
    width: '100%',
    height: '100%',
    maxWidth: '100%',
    maxHeight: '100%',
    objectFit: 'cover',
    border: '1px solid #2A363B',
    borderRadius: '10px',
  },
  movieImgWrapper:{
    height: '400px',
    width:'250px'
  },
  headingWrapper:{
    marginTop:'100px',
    marginLeft:'50px'
  }
}))

export default function Movie(){
    const classes = useStyles();
    const [loading, setLoading] = useState(false);
    const [reviews, setReviews] = useState([]);
    const[movie,setMovie] =useState([]);

    const location = useLocation();
    let movieId = new URLSearchParams(location.search).get('id') 


    useEffect(()=>{
       setLoading(true)
      fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=436a07c5838058f745a532d6f8cbc1d6`)
      .then(response => response.json())
      .then((data)=>{
          setMovie(data)
          setLoading(false)
        })

      },[setLoading, setMovie])

    useEffect(() => {
        setLoading(true);
        (async () => {
          try {
            const postsRef = firestore.collection("reviews");
            const postsDoc = await postsRef.where("movieId", "==", movieId).get();
            const data = postsDoc.docs.map((item) => ({
              id: item.id,
              ...item.data(),
            }));
            setReviews(data);
            setLoading(false);
          } catch (error) {
            setLoading(false);
          }
        })();
      }, [setLoading, setReviews]);
      console.log(movie)

    return(
        <>
        <div className={classes.headingWrapper}>
          <div className={classes.movieImgWrapper}>
            <img className={classes.searchResultPic}src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} ></img>
          </div>
          <Paper spacing={0} style={{backgroundColor: '#2A363B', marginBottom: '15px',height: '130px',padding: '0px'}}>
        <Grid item lg xs={12} container spacing={0} style={{ color:'white', height: '100%' }}>
            <Grid item xs={2} style={{ height: '100%' }} container direction='column' justify='center' alignItems='center'>
                <AccountCircleIcon style={{fontSize:'100px'}}/>
            </Grid>
            <Grid item xs={8} container style={{ height: '100%', paddingTop: '10px',paddingBottom: '10px', }}>
                <Grid item container direction='column'>
                <Grid item xs container drection='row'>
                    <Grid item xs>
                    <div className ={classes.reviewHeading}>
                        Rating: 8.4
                    </div>
                    </Grid>
                    <Grid item xs>
                    <div className ={classes.reviewHeading}>
                        Friend Rating: 6.9
                    </div>
                    </Grid>
                </Grid>
                <Grid item xs={10} >
                    <p
                    style={{ fontFamily: 'Raleway'}}>
                        Lorem ipsum dolor sit amet,unt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat
                    </p>
                </Grid>
                </Grid>
            </Grid>
            <Grid item xs={2} container>
                <Button style={{ textTransform: 'none', backgroundColor: '#E84A5F', color:'white',fontSize:'25px' }}
                item
                container
                fullWidth = 'true'
                direction='column'
                alignItems='center'
                justify='space-between'
                >
                Read Review
                </Button>
            </Grid>
        </Grid>
    </Paper>
        </div>
        <Paper spacing={0} style={{backgroundColor: '#FECEAB', marginLeft: '50px',marginRight: '50px', marginTop: '100px',height: '210px',padding: '20px', height: '80vh'}}>
            <ReviewList/>
        </Paper>
        </>
    )

}
