    }
    return(
        <div style={{backgroundColor:'#2A363B'}}>
            <div className= 'searchWrapper'>
                <Searchbar/>
                <div style={{paddingTop: '30px'}}>
                    <SearchResults results ={results} />
                </div>
            </div>


        </div>
    )
}
